import React from "react";
import "./styles.scss";

export const Login = (UserDetails, setUserDetails => {
  return (
    <div className="body">
      <form className="form">
        <h1 className="login-title">Login</h1>
        <input type="text" className="input" name="Username" placeholder="Username" onChange={(e) => handleUsernameChange(e)}></input>
        <input type="password" className="input" name="Password" onChange={(e) => handlePasswordChange(e)} placeholder="Password"></input>
        <button className="button" onClick={(e) => submit(e)}>Sign in</button>
        <p className="p">Lost your password?</p>
      </form>
      <div className="register">Don't have an account? <u>Sign up here!</u></div>
    </div>
  )


  function handleUsernameChange(e) {
    setUserDetails({ ...UserDetails, username: e.target.value })
    console.log(e)
  }

  function handlePasswordChange(e) {
    setUserDetails(...setUserDetails.password = e.target.value)
    console.log(e.target.value)
  }

  function submit(e) {
    alert(UserDetails.username)
    alert(UserDetails.password)
  }
});
