import React from "react";
import "./styles.scss";

export const Login = (userDetails, setUserDetails) => {
  const inputChange = (e) => {
    const a = (e) => {
      setUserDetails({ ...userDetails, username: e.target.value });
    };
  };
  const submit = (e) => {
    alert(userDetails.username);
    alert(userDetails.password);
  };

  return (
    <div className="body">
      <form className="form">
        <h1 className="login-title">Login</h1>
        <input
          type="text"
          className="input"
          name="Username"
          placeholder="Username"
          onChange={(e) => inputChange(e)}
        ></input>
        <input
          type="password"
          className="input"
          name="Password"
          onChange={(e) => inputChange(e)}
          placeholder="Password"
        ></input>
        <button className="button" onClick={(e) => submit(e)}>
          Sign in
        </button>
        <p className="p">Lost your password?</p>
      </form>
      <div className="register">
        Don't have an account? <u>Sign up here!</u>
      </div>
    </div>
  );
};
